    const bedtime = dateFns.parse(msg.kids_bedtime, 'HH:mm:ss', new Date());
    const showerTime = dateFns.subMinutes(bedtime, showerTimeMin);
    const showerTimeNotify = dateFns.subMinutes(showerTime, showerTimeNotifyMin);

    const isBedTime = dateFns.isSameHour(now,bedtime) && dateFns.isSameMinute(now,bedtime);
    const isShowerTime = dateFns.isSameHour(now,showerTime) && dateFns.isSameMinute(now,showerTime);
    const isShowerTimeNotify = dateFns.isSameHour(now,showerTimeNotify) && dateFns.isSameMinute(now,showerTimeNotify);

    msg.payload = {
        "showerTimeMin":showerTimeMin,
        "showerTimeNotifyMin":showerTimeNotifyMin,
        "states":{
            "bedTime": dateFns.format(bedtime,'MM/dd/yyyy HH:mm:ss'),
            "showerTime": dateFns.format(showerTime,'MM/dd/yyyy HH:mm:ss'),
            "showerTimeNotify": dateFns.format(showerTimeNotify,'MM/dd/yyyy HH:mm:ss')
        },
    }

    if (isBedTime) {
        node.status({fill:"green",shape:"dot",text:"bed time" + nodeStatus});
        return [ msg, null, null, null ];
    } else if (isShowerTime) {
        node.status({fill:"green",shape:"dot",text:"shower time" + nodeStatus});
        return [ null, msg, null, null ];
    } else if (isShowerTimeNotify) {
        node.status({fill:"green",shape:"dot",text:"shower time notify" + nodeStatus});
        return [ null, null, msg, null ];
